import React, { Component } from "react";
import updateArticleVoteReq from "../utils/updateVoteReq";

class Vote extends Component {
  // voted history takes {article or comment id: vote that is -1 or 1}
  state = { currentVote: 0, votedHistory: [] };

  handleVote = num => {
    this.setState(currentState => {
      const newVote = { [this.props.id]: num };
      const votedHistory = [...this.state.votedHistory];
      votedHistory.push(newVote);
      return { vote: currentState.vote + num, votedHistory };
    });
    const { path, id } = this.props;
    updateArticleVoteReq(id, num, path);
  };

  componentDidMount() {
    const { currentVote } = this.props;
    this.setState({ vote: currentVote });
  }

  render() {
    return (
      <div>
        <h3>Votes: {this.state.vote}</h3>
        <br />
        <button onClick={event => this.handleVote(1)}>Up Vote!</button>
        <button onClick={event => this.handleVote(-1)}>Down Vote!</button>
        <br />
      </div>
    );
  }
}

export default Vote;
