import React, { Component } from "react";

class SortCommentsDropDown extends Component {
  state = {
    sortBy: "Date_created"
  };

  handleSortByByChange(sortBy) {
    console.dir(this.state.sortBy, "<<<< sortBy");
    this.setState({ sortBy });
    console.log(this.state.sortBy, "<<<< state");
  }

  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <label>
            Pick your favorite flavor:
            <select
              value={this.state.value}
              onChange={this.handleSortByByChange}
            >
              <option value="grapefruit">Grapefruit</option>
              <option value="lime">Lime</option>
              <option value="coconut">Coconut</option>
              <option value="mango">Mango</option>
            </select>
          </label>
          <input type="submit" value="Submit" />
        </form>
      </div>
    );
  }
}

export default SortCommentsDropDown;
