import React, { Component } from "react";
import postComment from "../utils/postCommentReq";
import NoticeMsgDisplay from "./NoticeMsgDisplay";

class CommentForm extends Component {
  state = {
    bodyInput: "",
    postedComment: null
  };

  handleChange = event => {
    this.setState({ bodyInput: event.target.value });
  };

  handleSubmit = event => {
    event.preventDefault();
    const { bodyInput } = this.state;
    const { article_id } = this.props;
    postComment(article_id, bodyInput).then(postedComment => {
      this.setState({ postedComment });
    });
  };

  render() {
    return (
      <ul>
        {this.state.postedComment && (
          <NoticeMsgDisplay
            msg={`$this.state.postedComment has been posted!`}
          />
        )}

        <form onSubmit={this.handleSubmit}>
          <label name="studentName">
            Comment Body:
            <input
              type="text"
              onChange={this.handleChange}
              value={this.state.bodyInput}
              id="comment"
            />
          </label>
          <button>Add Comment</button>
        </form>
      </ul>
    );
  }
}

export default CommentForm;
