import axios from "axios";

export const postComment = (article_id, body) => {
  return axios
    .post(`/api/articles/${article_id}/comments`, {
      username: "jessjelly",
      body
    })
    .then(({ data: comment }) => {
      console.log(comment, "<<<< posted comment");
    });
};

export default postComment;
