import React, { Component } from "react";

class SortCommentsDropDown extends Component {
  state = {
    sortBy: "Date_created"
  };

  handleSortByByChange(event) {
    console.dir(event, "<<<< sortBy");
  }

  render() {
    return (
      <div>
        <form>
          <label>
            Sort comments by:
            <select
              value={this.state.value}
              onChange={this.handleSortByByChange}
            >
              <option value="date created">Grapefruit</option>
              <option value="comment_count">Comment Count</option>
              <option value="votes">Votes</option>
            </select>
          </label>
        </form>
      </div>
    );
  }
}

export default SortCommentsDropDown;
