import axios from "axios";

export const updateArticleVoteReq = (article_id, inc_votes, path) => {
  return (
    // /api/comments/:comment_id
    axios
      .patch(`https://nc-news2020.herokuapp.com/api/${path}/${article_id}`, {
        inc_votes
      })
      .then(({ data }) => {
        return data;
      })
  );
};

export default updateArticleVoteReq;
