import React, { Component } from "react";

class SortCommentsDropDown extends Component {
  state = {};

  handleSortByByChange(sortBy) {
    console.log(this.state.sortBy, "<<<< sortBy");
    this.setState({ sortBy });
    console.log(this.state.sortBy, "<<<< state");
  }

  render() {
    return (
      <div>
        <select>
          name="sort" // value={this.state.sortBy}
          onChange={event => this.handleSortByByChange(event.target.value)}>
          <option id="date_created">Sort by Date created</option>
          <option id="comment_count">Sort by Comment_count</option>
          <option id="votes">Sort by Votes</option>
        </select>
      </div>
    );
  }
}

export default SortCommentsDropDown;
