import React, { Component } from "react";

class SortCommentsDropDown extends Component {
  state = {
    sortBy: "Date_created"
  };

  handleSortByByChange(sortBy) {
    console.dir(this.state.sortBy, "<<<< sortBy");
    this.setState({ sortBy });
    console.log(this.state.sortBy, "<<<< state");
  }

  render() {
    return (
      <div>
        <select
          name="sort"
          value={this.state.sortBy}
          onChange={event => this.handleSortByByChange(event.target.value)}
        >
          <option id="date_created">Date created</option>
          <option id="comment_count">Comment_count</option>
          <option id="votes">Votes</option>
        </select>
      </div>
    );
  }
}

export default SortCommentsDropDown;
