import React, { Component } from "react";
import makeApiRequests from "../utils/api";

class ArticleComments extends Component {
  state = {
    comments: [],
    sortBy: "Date_created"
  };

  componentDidMount() {
    const { article_id } = this.props;
    makeApiRequests(`articles/${article_id}/comments`).then(comments => {
      this.setState({ comments });
    });
  }

  componentDidUpdate() {
    // /api/articles/1/comments?sort_by=comment_id
    const { article_id } = this.props;

    makeApiRequests(
      `articles/${article_id}/comments?sort_by=${this.state.sortBy}`
    ).then(comments => {
      this.setState({ comments });
    });
  }

  handleSortByByChange = ({ target: { value } }) => {
    this.setState({ sortBy: value });
  };

  render() {
    const {
      comments: { comments }
    } = this.state;

    if (comments) {
      return (
        <>
          <div>
            <form>
              <label>
                Sort comments by:
                <select
                  value={this.state.value}
                  onChange={this.handleSortByByChange}
                >
                  <option value="date created">Date Created</option>
                  <option value="comment_count">Comment Count</option>
                  <option value="votes">Votes</option>
                </select>
              </label>
            </form>
          </div>
          {comments.map(comment => {
            return (
              <>
                <hr />
                <br />
                <p>Body: {comment.body}</p>
                <p>Username: {comment.username}</p>
                <p>Votes: {comment.votes}</p>
                <p>Created at: {comment.created_at}</p>
                <br />
                <hr />
              </>
            );
          })}
          ;
        </>
      );
    } else return null;
  }
}

export default ArticleComments;
