import React, { Component } from "react";
import makeApiRequests from "../utils/api";
import { Link } from "@reach/router";
import { Button } from "react-bootstrap";

class Navbar extends Component {
  state = {
    allTopics: []
  };

  componentDidMount() {
    makeApiRequests("topics/").then(({ topics }) => {
      const allTopics = topics.map(topic => topic);
      this.setState({ allTopics });
    });
  }

  render() {
    <>
      <nav class="navbar navbar-expand-lg navbar-light bg-light">
        <a class="navbar-brand" href="#">
          Navbar
        </a>
        <button
          class="navbar-toggler"
          type="button"
          data-toggle="collapse"
          data-target="#navbarNavAltMarkup"
          aria-controls="navbarNavAltMarkup"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span class="navbar-toggler-icon"></span>
        </button>
        <div class="collapse navbar-collapse" id="navbarNavAltMarkup">
          <div class="navbar-nav">
            <a class="nav-item nav-link active" href="#">
              Home <span class="sr-only">(current)</span>
            </a>
            <a class="nav-item nav-link" href="#">
              Features
            </a>
            <a class="nav-item nav-link" href="#">
              Pricing
            </a>
            <a
              class="nav-item nav-link disabled"
              href="#"
              tabindex="-1"
              aria-disabled="true"
            >
              Disabled
            </a>
          </div>
        </div>
      </nav>
    </>;

    return this.state.allTopics.map((topic, index) => {
      return (
        <nav className="navbar" key={index}>
          <Link className="nav-link" to={`topics/${topic.slug}`}>
            {topic.slug}
          </Link>
        </nav>
      );
    });
  }
}

export default Navbar;
