import axios from "axios";

export const updateArticleVoteReq = (article_id, newVote) => {
  return (
    axios
      // /api/articles/1
      .patch(
        `https://nc-news2020.herokuapp.com/api/articles/${article_id}, ${newVote}`
      )
      .then(({ data }) => {
        return data;
      })
  );
};

export default updateArticleVoteReq;
